import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-stats',
  templateUrl: './stats.page.html',
  styleUrls: ['./stats.page.scss'],
})
export class StatsPage implements OnInit {
  cryptoId: string | null = null;
  cryptoData: any;

  constructor(private route: ActivatedRoute, private http: HttpClient) {}

  ngOnInit() {
    this.cryptoId = this.route.snapshot.paramMap.get('id');
    if (this.cryptoId) {
      this.getCryptoData();
    }
  }

  getCryptoData() {
    const url = `https://api.coingecko.com/api/v3/coins/${this.cryptoId}`;
    this.http.get(url).subscribe((data) => {
      this.cryptoData = data;
      console.log(this.cryptoData); // Vérifier les données dans la console
    });
  }
}
<ion-header>
  <ion-toolbar>
    <ion-title>Stats</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content>
  <ion-card *ngIf="cryptoData">
    <ion-card-header>
      <ion-card-title>{{ cryptoData.name }}</ion-card-title>
    </ion-card-header>
    <ion-card-content>
      <ion-list>
        <ion-item>
          <ion-label>Symbol</ion-label>
          <ion-note slot="end">{{ cryptoData.symbol }}</ion-note>
        </ion-item>
        <ion-item>
          <ion-label>Current Price</ion-label>
          <ion-note slot="end">{{ cryptoData.market_data.current_price.usd }}</ion-note>
        </ion-item>
        <ion-item>
          <ion-label>Market Cap</ion-label>
          <ion-note slot="end">{{ cryptoData.market_data.market_cap.usd }}</ion-note>
        </ion-item>
        <ion-item>
          <ion-label>24h Volume</ion-label>
          <ion-note slot="end">{{ cryptoData.market_data.total_volume.usd }}</ion-note>
        </ion-item>
      </ion-list>
    </ion-card-content>
  </ion-card>  <canvas #chartCanvas></canvas>
</ion-content>
